import classNames from 'classnames';
import React, {Component, PropTypes} from 'react';

/**
 * Component representing a container from a layout grid.
 *
 * @extends Component
 */
export default class Container extends Component {
  static propTypes = {

    // Set the container as fluid.
    fluid: PropTypes.bool,

    // Change the type of element used to generate the component.
    element: PropTypes.string
  };

  static defaultProps = {

    // Set the default element type.
    element: 'div'
  };

  render() {
    const {children, className, fluid, element, ...rest} = this.props;
    const Component = element;

    return (
      <Component {...rest} className={classNames({
        'container': !fluid,
        'container-fluid': fluid
      }, className)}>
        {children}
      </Component>
    );
  }
}
