import classNames from 'classnames';
import React, {Component, PropTypes} from 'react';

/**
 * Component representing a row from a layout grid.
 * In order to work properly it needs to be wrapped inside a <Container/> component.
 *
 * @extends Component
 */
export default class Row extends Component {
  static propTypes = {

    // Change the type of element used to generate the component.
    element: PropTypes.string
  };

  static defaultProps = {

    // Set the default element type.
    element: 'div'
  };

  render() {
    const {children, className, element, ...rest} = this.props;
    const Component = element;

    return (
      <Component className={classNames('row', className)}
        {...rest}>
        {children}
      </Component>
    );
  }
}
